import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Data } from 'src/app/classes/data';
import { Lesson } from 'src/app/classes/lesson';
import { ExercisesValueService } from 'src/app/services/exercises-value.service';
import { LessonService } from 'src/app/services/lesson.service';

@Component({
  selector: 'app-chapter-mgmt-details',
  templateUrl: './chapter-mgmt-details.component.html',
  styleUrls: ['./chapter-mgmt-details.component.css']
})
export class ChapterMgmtDetailsComponent {



  chapters!: Data
  chaptersDetails! : Lesson
  constructor(private route: ActivatedRoute,
              private router: Router,
              private lessonService: LessonService){

  }


  ngOnInit(): void {
    this.loadChapterDetails();
  }

  loadChapterDetails(){

    const id = +this.route.snapshot.paramMap.get('id')!;
    this.lessonService.getLessonList().subscribe(data=>{
      this.chapters = data;
     this.chaptersDetails = this.chapters.data.content[id-1];
    })
  }

  navigateToTamilVideo(){

    const objectString = JSON.stringify(this.chaptersDetails);
    this.router.navigate(['members','admin','videoTamilDetails'], { queryParams: { data: objectString } });
  }


  }

<div class="body">
    <button id="createButton">Create</button>
    <table>
        <thead>
            <tr>
                <th>SL.No</th>
                <th>Contents</th>
                <th>View Details</th>
            </tr>
        </thead>
        <tbody>

            <tr>
                <td>1</td>
                <td>Video Hindi</td>
                <td><button class="btn btn-primary" (click)="navigateToTamilVideo()">View Details</button></td>
            </tr>
            <tr>
                <td>2</td>
                <td>Video Tamil</td>
                <td><a (click)="navigateToTamilVideo()">View Details</a></td>
            </tr>

            <tr>
                <td>3</td>
                <td>MCQ</td>
                <td><a routerLink="/members/admin/quizdetails">View Details</a></td>
            </tr>

            <tr>
                <td>4</td>
                <td>Reading</td>
                <td><a routerLink="/members/admin/readingdetails">View Details</a></td>
            </tr>
            <tr>
                <td>5</td>
                <td>Vocabulary E to J</td>
                <td><a routerLink="/members/admin/vocabularyetojdetails">View Details</a></td>
            </tr>
            <tr>
                <td>6</td>
                <td>Vocabulary J to E</td>
                <td><a routerLink="/members/admin/vocabularyjtoedetails">View Details</a></td>
            </tr>


        </tbody>
    </table>
</div>
