import { Component } from '@angular/core';
import { ElementRef, ViewChild } from '@angular/core';


@Component({
  selector: 'app-createchapterdialog',
  templateUrl: './createchapterdialog.component.html',
  styleUrls: ['./createchapterdialog.component.css']
})
export class CreatechapterdialogComponent {

  @ViewChild('myModal') myModal!: ElementRef;

  openModal() {
    const modal: any = this.myModal.nativeElement;
    modal.show(); // Open the modal
  }
}
    <div class="container mt-5">
        <h1>Create Chapter</h1>

        <form>
            <div class="mb-3">
                <label for="chapterName" class="form-label">Chapter Name</label>
                <input type="text" class="form-control" id="chapterName" placeholder="Enter chapter name">
            </div>

            <div class="mb-3">
                <button type="submit" class="btn btn-primary">Save</button>
                <button type="button" class="btn btn-secondary">Cancel</button>
            </div>
        </form>
    </div>
