import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Lesson } from 'src/app/classes/lesson';
import { Reading } from 'src/app/classes/reading';
import { ExercisesValueService } from 'src/app/services/exercises-value.service';
import { LessonService } from 'src/app/services/lesson.service';

@Component({
  selector: 'app-readingdetails',
  templateUrl: './readingdetails.component.html',
  styleUrls: ['./readingdetails.component.css']
})
export class ReadingdetailsComponent {

  chapterDetails!: Lesson
  lesson!: Lesson
  reading1!: Reading;
  reading: Reading[]=[];
  id!: number;
  constructor(
    private exercises: ExercisesValueService,
    private router: Router,
    private lessonService: LessonService,
    private route: ActivatedRoute
  ){  }

  ngOnInit(): void {
    this.id = +this.route.snapshot.paramMap.get('id')!;
    this.loadChaptersDetails2();      
  }

  loadChaptersDetails2(){
    this.lessonService.getReadingByLessonId(this.id).subscribe((data)=>{
      let obj = data;
      if(typeof obj === 'object'){
      for(let key in obj){
        console.log(obj);
      }
      console.log(this.reading1.heading);
      }
    });

  }
  loadChaptersDetails(){
    this.route.queryParams.subscribe((params) => {
      if (params[`data`]) {
        this.lesson = JSON.parse(params[`data`]);
        console.log("checking the structure", this.lesson.name);
        if (this.lesson.reading[0] !== undefined){
        this.reading =  this.lesson.reading
        }
        else{
          this.reading[0].content = "No Content Found Found"
     //     this.reading[0].grammar[0].content = "N/A"
          this.reading[0].heading = "N/A"
          this.reading[0].imageUrl = "N/A"
          this.reading[0].subheading = "N/A"
        }
      }
      else{
        console.log("no data found");
      }
    });
  }

  backtochapterdetails(){
    this.router.navigate(['members','admin','chapterdetails',this.lesson.id]);
  }

}
