import { Component } from '@angular/core';
import { Lesson } from 'src/app/classes/lesson';
import { ExercisesValueService } from 'src/app/services/exercises-value.service';

@Component({
  selector: 'app-quizdetails',
  templateUrl: './quizdetails.component.html',
  styleUrls: ['./quizdetails.component.css']
})


export class QuizdetailsComponent {

  chapterDetails!: Lesson
  constructor(
    private exercises: ExercisesValueService,
  ){

  }

  ngOnInit(): void {
    this.loadChaptersDetails();      
  }

  loadChaptersDetails(){
    return this.exercises.ChapterDetails.subscribe(data=>{
      this.chapterDetails = data;
      this.chapterDetails.mcq[0];
    })
  }


}
<div class="body">
    <button id="createButton">Create</button>
    <table>
        <thead>
            <tr>
                <th>SL.No</th>
                <th>Question</th>
                <th>Option 1</th>
                <th>Option 2</th>
                <th>Option 3</th>
                <th>Option 4</th>
                <th>Answer</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>

            <tr *ngFor = "let items of chapterDetails.mcq; let i = index">
                <td>{{items.id}}</td>
                <td>{{items.question}}</td>
                <td>{{items.option1}}</td>
                <td>{{items.option2}}</td>
                <td>{{items.option3}}</td>
                <td>{{items.option4}}</td>
                <td>{{items.answer}}</td>
                <td><button class="delete-button">Edit</button>
                <button class="delete-button">Delete</button></td>
            </tr>

        </tbody>
    </table>
</div>
