import { Component } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { Addmcq } from 'src/app/classes/addmcq';
import { Lesson } from 'src/app/classes/lesson';
import { Mcq } from 'src/app/classes/mcq';
import { LessonService } from 'src/app/services/lesson.service';

@Component({
  selector: 'app-mcq-details',
  templateUrl: './mcq-details.component.html',
  styleUrls: ['./mcq-details.component.css']
})
export class McqDetailsComponent {

  createmcq!: Addmcq
  lesson!: Lesson
  mcq: Mcq[]=[];
  mcqQuestion = {
    question: '',
    option1: '',
    option2: '',
    option3: '',
    option4: '',
    answer: '',
  };

  constructor(
    private router: Router,
    private route: ActivatedRoute,
    private lessonService: LessonService
  ){  }

  ngOnInit(): void {
    this.loadMcqDetails();  
   }

   loadMcqDetails(){
     this.route.queryParams.subscribe((params) => {
       if (params[`data`]) {
         this.lesson = JSON.parse(params[`data`]);
         console.log("checking the structure", this.lesson.name);
         if (this.lesson.mcq[0] !== undefined){
         this.mcq =  this.lesson.mcq
         }
         else{
           this.mcq[0].question = "No Question Found Found"
           this.mcq[0].answer = "N/A"
           this.mcq[0].option1 = "N/A"
           this.mcq[0].option2 = "N/A"
           this.mcq[0].option3 = "N/A"
           this.mcq[0].option4 = "N/A"
         }
       }
       else{
         console.log("no data found");
       }
     });   
       }

  backtochapterdetails(){
    console.log("back buton value", +this.lesson)
    this.router.navigate(['members','admin','chapterdetails',this.lesson.id]);
  }

  onSubmit(form: any) {

    const submittedData = { ...form.value };
    this.createmcq = submittedData;
    this.createmcq.lesson_id = this.lesson.id;
    this.lessonService.postMcq(this.createmcq).subscribe((data=>{
      console.log();
    })
    );
    console.log("the submittedform value" +this.createmcq.question);

    //Clear the data after data is submitted
    this.mcqQuestion.question = '',
    this.mcqQuestion.option1 = '',
    this.mcqQuestion.option2 = '',
    this.mcqQuestion.option3 = '',
    this.mcqQuestion.option4 = '',
    this.mcqQuestion.answer = '',
    this.router.navigate(['members','admin','chapterdetails',this.lesson.id]);
      }
}
