import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Data } from 'src/app/classes/data';
import { LessonResponse } from 'src/app/classes/lesson-response';
import { LessonService } from 'src/app/services/lesson.service';

@Component({
  selector: 'app-lessons',
  templateUrl: './lessons.component.html',
  styleUrls: ['./lessons.component.css'],
})
export class LessonsComponent {

  lessonResponse!: LessonResponse<Data>;

  constructor(private router: Router,
              private routes: ActivatedRoute,
              private lessonservice: LessonService){

  }


  ngOnInit(): void {
      this.loadLesson();      
    }


  panelOpenState = false;
  showFiller = false;

safetyCheck(fn:any){
try{
  return fn();
}
catch(e){
  return undefined;
}
}

  loadLesson(){
    this.lessonservice.getLessonList().subscribe(data=>
      {
      this.lessonResponse = data;

      console.log("chapter name from api" +this.safetyCheck(()=>this.lessonResponse.data.Data.data[0].name));
    }

    );

  }

  loadvideo(){
    this.router.navigate(['members','lessons','video']);
  }
  loadvocabular(){
    this.router.navigate(['members','lessons','vocabulary']);
  }
  loadreading(){
    this.router.navigate(['members','lessons','reading']);
  }
  loadchoose(){
    this.router.navigate(['members','lessons','choose']);
  }
}
