import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  constructor(private router: Router){}

  lessons(){
    this.router.navigate(['members','lessons']);
  }
}
<div class="container col-xxl-8 px-4 py-5" style="background-color: #ffffff; margin-top: 1%;">
    <div class="row flex-lg-row-reverse align-items-center g-5 py-5">
        <div class="col-10 col-sm-8 col-lg-6 image-container">
            <img src="assets/images/Nihongo Shiksha Hero-Section.jpg" id ="hero" class="d-block mx-lg-auto img-fluid"
                alt="Bootstrap Themes" width="1000" height="200" style="margin-left: 27px; margin-top: -80px;" loading="lazy">
        </div>
        <div class="col-lg-6" style="color: #28387a;">
            <h3 class="display-6 fw-bold lh-1 mb-3">Learn Japanese in Urdu, Hindi & Tamil!</h3>
            <p class="lead"><strong>Unlock the beauty of Japanese language from your home.
                Start mastering now!"</strong></p>
            <div class="d-grid gap-2 d-md-flex justify-content-md-start">
                <button type="button" class="btn btn-custom-success btn-lg px-4 me-md-2">Get Started Here</button>
                <button type="button" class="btn btn-custom-outline-success btn-lg px-4 text-body-success">Try Free
                    Lessons</button>
            </div>
        </div>
    </div>
</div>
<button (click)="lessons()">navigate to lessons</button>
