import { Component } from '@angular/core';
import { chapter } from 'src/app/classes/chapter';
import { Data } from 'src/app/classes/data';
import { Lesson } from 'src/app/classes/lesson';
import { ExercisesValueService } from 'src/app/services/exercises-value.service';
import { LessonService } from 'src/app/services/lesson.service';

@Component({
  selector: 'app-chapter-mgmt',
  templateUrl: './chapter-mgmt.component.html',
  styleUrls: ['./chapter-mgmt.component.css']
})
export class ChapterMgmtComponent {

  chaptersData!: Data;
  editChapterName = new chapter();


  constructor(private lessonservice: LessonService,
    private exercises: ExercisesValueService,) { }

  ngOnInit(): void {
    this.loadChapters();
  }


  loadChapters() {

    return this.lessonservice.getLessonList().subscribe(data => {
      this.chaptersData = data;
      console.log("chapter name from api" + this.chaptersData.data.content[0].id);

    }

    );


  }

  loadDetails(item: Lesson) {
    this.exercises.ChapterDetails.next(item);

  }
  editChapter(lessonId: number) {

    this.editChapterName.name = this.chaptersData.data.content[lessonId -1].name;
    this.editChapterName.id = this.chaptersData.data.content[lessonId -1].id;
    console.log("Selected chapter is " + this.chaptersData.data.content[lessonId - 1].name);

  }



  onSubmit(form: any) {
    // Access form data using form.value and assign it to a variable
    const submittedData = { ...form.value };

    this.formData.chapterName = submittedData.chapterName;
    // You can now use 'submittedData' in your component as needed
    console.log('Submitted Data:', this.formData.chapterName);
    this.lessonservice.putLesson(this.editChapterName).subscribe(data=>{
      console.log(data);
    });
  }
}
<div class="body">
    <app-createchapterdialog></app-createchapterdialog>
    <table>
        <thead>
            <tr>
                <th>SL.No</th>
                <th>Chapter Name</th>
                <th>View Details</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>

            <tr *ngFor = "let items of chaptersData.data.content; let i = index">
                <td>{{items.id}}</td>
                <td>{{items.name}}</td>

                <td><a routerLink="/members/admin/chapterdetails" (click)="loadDetails(items)">View Details</a></td>
                <td><button class="delete-button" (click)="editChapter(items.id)" data-bs-toggle="modal" data-bs-target="#myEditChapterModal">Edit</button>
                <button class="delete-button">Delete</button></td>
            </tr>

        </tbody>
    </table>
</div>


<!-- Edit Dialog box Start-->



<!-- Modal -->
<div class="modal fade" id="myEditChapterModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div class="modal-dialog">
        <div class="modal-content">
            <div class="modal-header">
                <h5 class="modal-title" id="exampleModalLabel"> Edit Chapter Name </h5>
                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div class="modal-body">

                <!--Insert Form Code Start-->
                <form #chapterForm="ngForm" (ngSubmit)="onSubmit(chapterForm)">
                    <div class="mb-3 row">
                        <label for="chapterName" class="col-sm-2 col-form-label">Chapter Name</label>
                        <div class="col-sm-10">
                            <input type="text" class="form-control" id="chapterName" placeholder="Enter chapter name" name="chapterName" [(ngModel)]="editChapterName.name" required>
                            <input type="hidden" class="form-control" id="chapterId"  name="chapterId" [(ngModel)]="editChapterName.id" required>
                        </div>
                    </div>
                    <div class="modal-footer">
                        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Cancel</button>
                        <button type="submit" class="btn btn-primary" data-bs-dismiss="modal" >Save</button>
                    </div>
                </form>
                <!--Insert Form Code End-->
            </div>
        </div>
    </div>

    <!--End of Edit Dialog box-->
