import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { LessonService } from 'src/app/services/lesson.service';

@Component({
  selector: 'app-lessons',
  templateUrl: './lessons.component.html',
  styleUrls: ['./lessons.component.css'],
})
export class LessonsComponent {

  chapterName: String = "";

  constructor(private router: Router,
              private lessonservice: LessonService){

  }


  ngOnInit(): void {
      this.loadLesson();
    }

  panelOpenState = false;
  showFiller = false;


  loadLesson(){
    this.lessonservice.getLessonList().subscribe(data=>{
      this.chapterName = data.test;
      console.log("chapter name from api " +this.chapterName +"and " +data.test)
    }
    )
  }

  loadvideo(){
    this.router.navigate(['members','lessons','video']);
  }
  loadvocabular(){
    this.router.navigate(['members','lessons','vocabulary']);
  }
  loadreading(){
    this.router.navigate(['members','lessons','reading']);
  }
  loadchoose(){
    this.router.navigate(['members','lessons','choose']);
  }
}
