import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Lesson } from 'src/app/classes/lesson';
import { Vocabulary } from 'src/app/classes/vocabulary';
import { ExercisesValueService } from 'src/app/services/exercises-value.service';
import { LessonService } from 'src/app/services/lesson.service';

@Component({
  selector: 'app-vocabularyjtoedetails',
  templateUrl: './vocabularyjtoedetails.component.html',
  styleUrls: ['./vocabularyjtoedetails.component.css']
})
export class VocabularyjtoedetailsComponent {

  vocabularetojDetails!: Lesson

  chapterDetails!: Lesson
  lesson!: Lesson
  vocabularjtoe: Vocabulary[]=[]
  constructor(
    private router: Router,
    private route: ActivatedRoute
  ){}


  ngOnInit(): void {
    this.loadChaptersDetails();      
  }

  loadChaptersDetails(){
    this.route.queryParams.subscribe((params) => {
      if (params[`data`]) {
        this.lesson = JSON.parse(params[`data`]);
        console.log("checking the structure", this.lesson.vocabulary[0].english_character);
        if (this.lesson.vocabularyetoj[0] !== undefined){
        this.vocabularjtoe =  this.lesson.vocabulary
        }
      }
        });
  }

  backtochapterdetails(){
    this.router.navigate(['members','admin','chapterdetails',this.lesson.id]);
  }


}
<div class="body">
    <button id="createButton">Create</button>
    <button id="createButton" (click)="backtochapterdetails()">Back to chapterDetails </button>
    <table>
        <thead>
            <tr>
                <th>SL.No</th>
                <th>English</th>
                <th>Hiragana</th>
                <th>Kanji</th>
                <th>Romaji</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>

            <tr *ngFor = "let items of vocabularjtoe; let i = index">
                <td>{{items.id}}</td>
                <td>{{items.english_character}}</td>
                <td>{{items.hiragana_character}}</td>
                <td>{{items.kanji_character}}</td>
                <td>{{items.romaji_character}}</td>
                <td><button class="edit-button">Edit</button>
                <button class="delete-button">Delete</button></td>
            </tr>

        </tbody>
    </table>
</div>
