import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Lesson } from 'src/app/classes/lesson';
import { ExercisesValueService } from 'src/app/services/exercises-value.service';

@Component({
  selector: 'app-readingdetails',
  templateUrl: './readingdetails.component.html',
  styleUrls: ['./readingdetails.component.css']
})
export class ReadingdetailsComponent {

  chapterDetails!: Lesson
  constructor(
    private exercises: ExercisesValueService,
    private router: Router,
  ){

  }

  ngOnInit(): void {
    this.loadChaptersDetails();      
  }

  loadChaptersDetails(){
    return this.exercises.ChapterDetails.subscribe(data=>{
      this.chapterDetails = data;
      this.chapterDetails.reading[0];
    })
  }

  backtochapterdetails(){
    this.router.navigate(['members','admin','chapterdetails']);
  }

}
<div class="body">
    <button id="createButton">Create</button>
    <button id="createButton" (click)="backtochapterdetails()">Back to chapterDetails </button>
    <table>
        <thead>
            <tr>
                <th>SL.No</th>
                <th>Heading</th>
                <th>Sub Heading 1</th>
                <th>Grammar</th>
                <th>Image URL 3</th>
                <th>Content </th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>

            <tr *ngFor = "let items of chapterDetails.reading; let i = index">
                <td>{{items.id}}</td>
                <td>{{items.heading}}</td>
                <td>{{items.subheading}}</td>
                <td>{{items.grammar[0].content}}</td>
                <td>{{items.imageUrl}}</td>
                <td>{{items.content}}</td>
                <td><button class="edit-button">Edit</button>
                <button class="delete-button">Delete</button></td>
            </tr>

        </tbody>
    </table>
</div>
