import { ChangeDetectorRef, Component } from '@angular/core';
import { DomSanitizer, SafeResourceUrl } from '@angular/platform-browser';
import { EMPTY } from 'rxjs';
import { Video } from 'src/app/classes/video';
import { ExercisesValueService } from 'src/app/services/exercises-value.service';
@Component({
  selector: 'app-video',
  templateUrl: './video.component.html',
  styleUrls: ['./video.component.css']
})
export class VideoComponent {


  safeSrc!: SafeResourceUrl;


  ngOnInit(): void {
  }

  ngOnChange(): void {
    this.getUrlValue();
  }

  constructor(private exercises: ExercisesValueService,
    private sanitizer: DomSanitizer) {
    this.getUrlValue();
  }


  getUrlValue() {

    if (this.exercises.VideoValue != "" ||
      this.exercises.VideoValue != undefined ||
      this.exercises.VideoValue != EMPTY)
        {
          this.safeSrc = this.sanitizer.bypassSecurityTrustResourceUrl(this.exercises.VideoValue);
          this.exercises.VideoValue = "";
        }
        else{
          this.exercises.VideoValue2.subscribe(data=>{
            this.safeSrc = this.sanitizer.bypassSecurityTrustResourceUrl(data);
          })

        }


  }
}
