import { Component } from '@angular/core';
import { Lesson } from 'src/app/classes/lesson';
import { ExercisesValueService } from 'src/app/services/exercises-value.service';

@Component({
  selector: 'app-chapter-mgmt-details',
  templateUrl: './chapter-mgmt-details.component.html',
  styleUrls: ['./chapter-mgmt-details.component.css']
})
export class ChapterMgmtDetailsComponent {


  chapterDetails!: Lesson
  constructor(
    private exercises: ExercisesValueService,
  ){

  }


  ngOnInit(): void {
    this.loadChaptersDetails();      
  }

  loadChaptersDetails(){
    return this.exercises.ChapterDetails.subscribe(data=>{
      this.chapterDetails = data;
    })
  }

}
<div class="body">
    <button id="createButton">Create</button>
    <table>
        <thead>
            <tr>
                <th>SL.No</th>
                <th>Chapter Name</th>
                <th>Edit</th>
                <th>View Details</th>
                <th>Delete</th>
            </tr>
        </thead>
        <tbody>
            <!--
            <tr *ngFor = "let data of chaptersData.data.content; let i = index">
                <td>{{data.id}}</td>
                <td>{{data.name}}</td>
                <td><a href="#">Edit</a></td>
                <td><a routerLink="/members/admin/chapterdetails">View Details</a></td>
                <td><button class="delete-button">Delete</button></td>
            </tr>
            -->

        </tbody>
    </table>
</div>
