  selector: 'app-choose',
  templateUrl: './choose.component.html',
  styleUrls: ['./choose.component.css']
})
export class ChooseComponent {

  question!:string;
  option1!:string;
  option2!:string;
  option3!:string;
  option4!:string;
  mcq!: Mcq[];
  constructor(private exercise: ExercisesValueService){
    this.loadMcqQuestion();

  }



  selectedAnswer(){

