    this.exercise.McqValue.subscribe(data=>{
      this.mcq = data;
      this.question = this.mcq[0].question;
    this.option1 = this.mcq[0].option1;
    this.option2 = this.mcq[0].option2;
    this.option3 = this.mcq[0].option3;
    this.option4 = this.mcq[0].option4;
    })
  }

