import { animate, state, style, transition, trigger } from '@angular/animations';
import { Component } from '@angular/core';
import { ResultService } from 'src/app/services/result.service';

@Component({
  selector: 'app-dialogbox',
  templateUrl: './dialogbox.component.html',
  styleUrls: ['./dialogbox.component.css'],
  animations: [
    trigger('rotateAnimation', [
      state('open', style({ transform: 'rotate(0deg)' })),
      state('close', style({ transform: 'rotate(180deg)' })),
      transition('open <=> close', animate('0.5s ease-in-out')),
    ]),
  ],
})


export class DialogboxComponent {

  animationState = 'open'; // Initial state

  constructor(private resultService: ResultService,){}

  ngOnInit(): void {
    this.openModal();
  }

  showModal = false;

  openModal() {
    this.resultService.openDialog.subscribe(data=>{
      this.showModal = data;
      animationState = 'open';
    });
  }

  closeModal() {
    this.showModal = false;
  }

}
