import { Component } from '@angular/core';
import {ElementRef, ViewChild } from '@angular/core';


@Component({
  selector: 'app-dialogform',
  templateUrl: './dialogform.component.html',
  styleUrls: ['./dialogform.component.css']
})
export class DialogformComponent {
  @ViewChild('myModal') myModal!: ElementRef;
  openModal() {
    const modal: any = this.myModal.nativeElement;
    modal.show(); // Open the modal
  }

}
<button type="button" (click)= openModal() class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#myModal">
        Create Chapter
    </button>

    <!-- Modal -->
    <div class="modal fade" id="myModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div class="modal-dialog">
            <div class="modal-content">
                <div class="modal-header">
                    <h5 class="modal-title" id="exampleModalLabel">Popup Window</h5>
                    <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                </div>
                <div class="modal-body">
                    <p>This is a popup window content.</p>
                </div>
                <div class="modal-footer">
                    <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                    <button type="button" class="btn btn-primary">Save changes</button>
                </div>
            </div>
        </div>
    </div>
