import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-lessons',
  templateUrl: './lessons.component.html',
  styleUrls: ['./lessons.component.css'],
})
export class LessonsComponent {

  constructor(private router: Router){


  }

  showFiller = false;

  loadchapters(){
    this.router.navigate(['members','lessons','chapters']);
  }
}
<mat-drawer-container class="example-container" style="margin-top: 1%;" autosize>
    <mat-drawer #drawer class="example-sidenav" mode="over">
        <mat-icon aria-hidden="false" aria-label="Example close icon" 
        fontIcon="close" (click)="drawer.toggle();">
        </mat-icon>
      <h3>Chapters</h3>
      <hr>

        <button (click)="loadchapters(); drawer.toggle();">load chapter</button>

    </mat-drawer>

    <div class="example-sidenav-content">
     <div class="sidemenu">
        <mat-icon aria-hidden="false" aria-label="Example menu icon" 
        fontIcon="menu" (click)="drawer.toggle();">
        </mat-icon>
    </div>
    <div class="dynamic-content">
        <router-outlet></router-outlet>  
    </div>    
    </div>


  </mat-drawer-container>
